// --- RENDER HTML SITEMAP ---
function renderSitemap(filter = "") {
    const container = document.getElementById('sitemapContent');
    let html = '';
    const search = filter.toLowerCase();

    for (const [category, pages] of Object.entries(sitemapData)) {
        // Filter pages based on search
        const filteredPages = pages.filter(p => p.name.toLowerCase().includes(search));

        if (filteredPages.length > 0) {
            html += `



 ${category}


            `;

            filteredPages.forEach(page => {
                html += `

                     ${page.name}
                `;
            });

            html += ``;
        }
    }

    if(html === '') html = '
No pages found.';
    container.innerHTML = html;
}

// Initial Render
renderSitemap();

// Search Event
document.getElementById('sitemapSearch').addEventListener('input', (e) => {
    renderSitemap(e.target.value);
});

// --- ADVANCED: XML GENERATOR ---
function generateXML() {
    // Change this to your real domain
    const baseURL = "https://xizoahub.com/"; 
    const date = new Date().toISOString().split('T')[0];

    let xml = '\n';
    xml += '\n';

    // Loop through all data
    for (const [category, pages] of Object.entries(sitemapData)) {
        pages.forEach(page => {
            xml += '  \n';
            xml += `    ${baseURL}${page.link}\n`;
            xml += `    ${date}\n`;
            xml += '    monthly\n';
            xml += '    0.8\n';
            xml += '  \n';
        });
    }

    xml += '';

    document.getElementById('xmlOutput').value = xml;
    document.getElementById('xmlModal').style.display = 'flex';
}
